const escapeHTML = str => {
    let inCodeBlock = false;
    let escapedStr = '';

    for (let i = 0; i < str.length; i++) {
        if (str[i] === '`' && str[i - 1] !== '`') {
            inCodeBlock = !inCodeBlock;
        }

        if (!inCodeBlock) {
            switch (str[i]) {
                case '&':
                    escapedStr += '&amp;';
                    break;
                case '<':
                    escapedStr += '&lt;';
                    break;
                case '>':
                    escapedStr += '&gt;';
                    break;
                case "'":
                    escapedStr += '&#39;';
                    break;
                case '"':
                    escapedStr += '&quot;';
                    break;
                default:
                    escapedStr += str[i];
            }
        } else {
            escapedStr += str[i];
        }
    }

    return escapedStr;
};

const DOMPurify = createDOMPurify(new jsdom.JSDOM('').window);
msg.parseMarkdown = (md) =>
    DOMPurify.sanitize(marked.parse(escapeHTML(md)));

return msg;

// // check for any pending content
