const { DocxLoader } = langchainDocumentLoadersFsDocx;
const { DirectoryLoader } = langchainDocumentLoadersFsDirectory;
const { RecursiveCharacterTextSplitter  } = langchainTextSplitter;

const loader = new DirectoryLoader(
    msg.directory,
    {
        ".docx": (path) => new DocxLoader(path)
    }
);

const splitter = new RecursiveCharacterTextSplitter();

(async () => {
    try {
        let docs = await loader.load();
        docs = await splitter.splitDocuments(docs);
        node.send({
            action: "vector-store-update-index",
            docs
        });
    }
    catch (e) {
        node.error(e.stack);
    }
})();

msg.systemPrompt = `You are a helpful chat bot that will perform any requested tasks to the best of your ability.`;

msg.contextPrompt = (context) => `The following documents will provide you useful context when performing your task: 
${
    context.map(doc => {
        return `
----- BEGIN DOCUMENT: ${doc.metadata.source} -----

${doc.pageContent}

----- END DOCUMENT -----
`;
    })
}
`

return msg;

msg.vectorSearchParams = {
    top: 4
};

return msg;
