const toInject = Object.fromEntries(
    env.get("Keys").map(k => [k, flow.get(`$parent.${k}`)])
);

msg = {
    ...msg,
    ...toInject
};

return msg;

// Setup
// const http = require('http');
// const https = require('https');

const { Event } = msg.global;

class CustomEvent extends Event { 
  constructor(message, data) {
    super(message, data)
    this.detail = data?.detail
  }
}

const SSE = function (url, options) {
  if (!(this instanceof SSE)) {
    return new SSE(url, options);
  }

  if (!url) {
    throw new Error('SSE - URL is required');
  }

  this.INITIALIZING = -1;
  this.CONNECTING = 0;
  this.OPEN = 1;
  this.CLOSED = 2;

  this.url = url;

  options = options || {};
  this.headers = options.headers || {};
  this.payload = options.payload !== undefined ? options.payload : '';
  this.method = options.method || (this.payload && 'POST' || 'GET');
  this.withCredentials = !!options.withCredentials;
  this.debug = !!options.debug;

  this.body = this.headers['Content-Type'] === "application/json" ? JSON.stringify(this.payload) : this.payload.toString();

  this.FIELD_SEPARATOR = ':';

  this.listeners = {};

  this.addEventListener = function(type, listener) {
    if (this.listeners[type] === undefined) {
      this.listeners[type] = [];
    }

    if (this.listeners[type].indexOf(listener) === -1) {
      this.listeners[type].push(listener);
    }
  };

  this.removeEventListener = function(type, listener) {
    if (this.listeners[type] === undefined) {
      return;
    }

    var filtered = [];
    this.listeners[type].forEach(function(element) {
      if (element !== listener) {
        filtered.push(element);
      }
    });
    if (filtered.length === 0) {
      delete this.listeners[type];
    } else {
      this.listeners[type] = filtered;
    }
  };

  this.dispatchEvent = function(e) {
    if (!e) {
      return true;
    }

    if (this.debug) {
      console.debug(e);
    }

    e.source = this;

    var onHandler = 'on' + e.type;
    if (this.hasOwnProperty(onHandler)) {
      this[onHandler].call(this, e);
      if (e.defaultPrevented) {
        return false;
      }
    }

    if (this.listeners[e.type]) {
      return this.listeners[e.type].every(function(callback) {
        callback(e);
        return !e.defaultPrevented;
      });
    }

    return true;
  };

  this._setReadyState = function(state) {
    var event = new CustomEvent('readystatechange');
    event.readyState = state;
    this.readyState = state;
    this.dispatchEvent(event);
  };

  this._onStreamFailure = function(e) {
    var event = new CustomEvent('error');
    event.data = e.message;
    this.dispatchEvent(event);
    this.close();
  }

  this._onStreamAbort = function(e) {
    this.dispatchEvent(new CustomEvent('abort'));
    this.close();
  }

  this._onStreamProgress = function(response) {
    if (this.readyState == this.CONNECTING) {
      this.dispatchEvent(new CustomEvent('open'));
      this._setReadyState(this.OPEN);
    }

    var data = response.toString();
    this.dispatchEvent(this._parseEventChunk(data));
  };

  this._onStreamLoaded = function(response) {
    this._onStreamProgress(response);
  };

  this._parseEventChunk = function(chunk) {
    if (!chunk || chunk.length === 0) {
      return null;
    }

    if (this.debug) {
      console.debug(chunk);
    }

    var e = {'id': null, 'retry': null, 'data': null, 'event': null};
    chunk.split(/\n|\r\n|\r/).forEach(function(line) {
      var index = line.indexOf(this.FIELD_SEPARATOR);
      var field, value;
      if (index > 0) {
        var skip = (line[index+1] === ' ') ? 2 : 1;
        field = line.substring(0, index);
        value = line.substring(index + skip);
      } else if (index < 0) {
        field = line;
        value = '';
      } else {
        return;
      }

      if (!(field in e)) {
        return;
      }

      if (field === 'data' && e[field] !== null) {
          e['data'] += "\n" + value;
      } else {
        e[field] = value;
      }
    }.bind(this));

    var event = new CustomEvent(e.event || 'message');
    event.data = e.data || '';
    event.id = e.id;
    return event;
  };

  this._checkStreamClosed = function() {
    if (this.readyState === this.CLOSED) {
      return;
    }
  };

  this.stream = function() {
    if (this.readyState === this.CONNECTING) {
      node.error("Already connecting.");
      return;
    }

    node.log("Request start");

    this._setReadyState(this.CONNECTING);

    var protocol = this.url.startsWith('https') ? https : http;
    var req = protocol.request(this.url, {
      method: this.method,
      headers: this.headers
    }, (res) => {
      const onError = (e) => {
        node.log(`Response error: ${e}`)
        this._onStreamFailure(e);
      };
      if (res.statusCode >= 400) {
        onError(new Error(`Received status: ${res.statusCode}`));
      }
      res.on('error', onError);
      res.on('data', (chunk) => {
        try {
          //node.log("Receiving data");
          this._onStreamProgress(chunk);
        }
        catch (e) {
          node.error(e);
          node.error(e.stack);
        }
      });
      res.on('end', () => {
        try {
          node.log("Request end");
          this.close();
        }
        catch (e) {
          node.error(e);
          node.error(e.stack);
        }
      });
    });

    req.on('timeout', e => node.log(`Request timeout: ${e}`));

    req.on('error', (e) => {
      try {
        node.error(`Reqeust error: ${e}`);
        this._onStreamFailure(e);
      }
      catch (e) {
        node.error(e);
        node.error(e.stack);
      }
    });

    if (["POST", "PUT", "PATCH"].includes(this.method)) {
        node.log(`Writing body: ${this.body}`);
        // Write data to request body
        req.write(this.body);
    }

    req.end();
  };

  this.close = function() {
    if (this.readyState === this.CLOSED) {
      return;
    }

    this._setReadyState(this.CLOSED);
  };

  if (options.start === undefined || options.start) {
    this.stream();
  }
};

return {
  ...msg,
  SSE
}

const id = env.get("Id");
const url = env.get("Url")
const defaultOptions = env.get("Options")

const msgOptions = msg[id] ?? {};

// check msg for more options
const options = {
  ...defaultOptions,
  ...msgOptions,
  headers: {
    ...defaultOptions.headers,
    ...msgOptions.headers
  }
}

node.log(`Sending request: ${id} - ${url}`);
node.log(`Options: ${JSON.stringify(options, undefined, 2)}`);

node.send([undefined, {
  id,
  url,
  options,
  msg
}]);

const source = msg.SSE(url, options);
msg.source = source;

source.addEventListener('message', function(e) {
  // Assuming we receive JSON-encoded data payloads:
  const payload = e.data;
  node.send({
    type: 'sse-message',
    payload
  });
});

let error = null;
source.addEventListener('error', e => {
  error = e;
  node.send({
    type: 'sse-error',
    error
  });
})

const states = Object.fromEntries(
  ["INITIALIZING", "CONNECTING", "OPEN", "CLOSED"].map(it => [source[it], it])
);

const wait = t => new Promise(r => setTimeout(r, t));

source.addEventListener('readystatechange', async e => {
  if (e.readyState === source.CLOSED) {
    // wait 1 second to allow Node-RED to finish
    // processing any streamed content that just came in
    //await wait(1000);
    node.send({
      type: 'sse-closed'
    })
  }
});

msg.type = 'sse-connecting';
return msg;

// if this is a forward messge
if (msg.action == "forward-msg") {
    // save our msg for later
    msg.action = msg.msgAction;
    delete msg.msgAction;
    context.set("forwardedMsg", msg);
    // don't send anything for now
    return;
}

// get any forwarded input we might have
const forwardedMsg = context.get("forwardedMsg");

return {
    ...msg,
    msgIn: forwardedMsg
};

return {
    ...msg,
    action: "forward-msg",
    msgAction: msg.action
};

// Allow combination of msg._ui and this node allowing chaining of the nodes
if (!Array.isArray(msg._ui)) {
    msg._ui = [];
}

// If no mode specified, we assume the desire is to update (since a removal attempt with nothing to remove is safe)
if ( !msg.mode ) msg.mode = 'update'

// If mode is remove, then simply do that and return
if ( msg.mode === 'delete' || msg.mode === 'remove' ) {
    if (!msg.elementId) {
        node.warn('[uib-element:buildUi] Cannot remove element as no HTML ID provided')
        return
    }

    msg._ui.push({
        'method': 'removeAll',
        'components': [
            `#${msg.elementId}`,
        ]
    })

    return msg;
}

// If no HMTL ID is specified & not deleting, then always ADD
if (!msg.elementId) {
    msg.mode = 'add'
}

// Otherwise ...

// Add the outer component which is always a div
msg._ui.push({
    'method': msg.mode === 'add' ? 'add' : 'replace',
    'components': [],
} )
msg.parent = msg._ui[msg._ui.length - 1]

return msg;

msg.elementId = env.get("HTML Id");
msg.parentElement = env.get("Parent");
msg.heading = env.get("Title");
return msg;

const parent = msg.parent;

// Add the form tag
parent.components.push({
    'type': 'form',
    'parent': msg.parentElement,
    'id': msg.elementId,
    'components': [],
    'attributes': {
        'class': 'chat-ui'
    },
})

// Convenient references
const frmBody = parent.components[parent.components.length - 1]

// Add the header
frmBody.components.push({
    'type': 'h1',
    'slot': msg.heading,
})

// Add the chat history div to the form body components
frmBody.components.push(...msg.provideUi.chatElements(msg.elementId, msg.history))

// Add a div to wrap the textarea and send button
const formDiv = {
    'type': 'div',
    'attributes': {
        'class': 'message-form'
    },
    'components': msg.provideUi.formElements(msg.elementId),
}

// Add the message div to the form body components
frmBody.components.push(formDiv)

return msg;

