@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.loading {
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  display: block;
  width: 50px;
  height: 50px;
  animation: spin 2s linear infinite;
}

.chat-ui {
    display: flex;
}

.chat-history {
    display: block;
    flex: 1;
    overflow: auto;
}

.chat-history .role {
    font-weight: bold;
    text-transform: capitalize;
}

.chat-history .role:after {
    content: ": ";
}

.message-form {
    display: flex;
    flex: 0;
    min-height: 100px;
    padding: 10px;
}

.message-form *:disabled {
    opacity: .5;
}

.message-form textarea {
    flex: 1;
    padding: 10px;
    height: 100%;
}

.message-form button {
    width: 80px;
    height: 100%;
}

