msg.elementId = env.get("HTML Id");
msg.mode = "replace";
return msg;

const parent = msg.parent;

// only if this event came from our form
if (!(`${msg.elementId}-next-message` in (msg.payload ?? {}))) {
    return;
}

// Add a div to wrap the textarea and send button
parent.components.push(...msg.provideUi.formElements(msg.elementId));

return msg;

msg.elementId = env.get("HTML Id");
msg.mode = "replace";
return msg;

const parent = msg.parent;

// Add the chat history div to the form body components
parent.components.push(...msg.provideUi.chatElements(msg.elementId, msg.history))


return msg;

msg.elementId = env.get("HTML Id");
msg.mode = "replace";
return msg;

const parent = msg.parent;

const elements = msg.provideUi.formElements(msg.elementId)

if (msg.history.streaming) {
    elements.forEach(it => {
        it.attributes = {
            ...it.attributes,
            disabled: "disabled"
        }
    });

    const button = elements.find(it => it.id === `${msg.elementId}-send-button`);

    button.slot = `
        <span class="loading"></span>
    `
}

// Add a div to wrap the textarea and send button
parent.components.push(...elements);

return msg;

msg.elementId = env.get("HTML Id");
msg.mode = "replace";
return msg;

const parent = msg.parent;

// we are already steaming, find our chat
const current = msg.history.entries.find(it => it.id === msg.history.streaming);

parent.components.push({
    'type': 'span',
    'id': `message-${current.id}`,
    'slot': msg.parseMarkdown(current.content),
    'attributes': {
        'class': 'message'
    }
});

return msg;

if (!msg.provideUi) {
    msg.provideUi = {};
}
msg.provideUi.formElements = (elementId) => [
    {
        'type': 'textarea',
        'id': `${elementId}-next-message`,
        'attributes': {
            'placeholder': 'Type your message here...',
        },
    },
    {
        'type': 'button',
        'id': `${elementId}-send-button`,
        'attributes': {
            type: 'button',
            title: 'Send the form data back to Node-RED'
        },
        'events': {
            click: 'uibuilder.eventSend'
        },
        'slot': `<svg xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 512 512">
        <!--!Font Awesome Free 6.5.1 by @fontawesome -
            https://fontawesome.com License -
            https://fontawesome.com/license/free Copyright 2024
            Fonticons, Inc.-->
        <path fill="#74C0FC" d="M498.1 5.6c10.1 7 15.4 19.1 13.5
            31.2l-64 416c-1.5 9.7-7.4 18.2-16 23s-18.9 5.4-28 1.6L284
            427.7l-68.5 74.1c-8.9 9.7-22.9 12.9-35.2 8.1S160 493.2 160
            480V396.4c0-4 1.5-7.8 4.2-10.7L331.8
            202.8c5.8-6.3 5.6-16-.4-22s-15.7-6.4-22-.7L106 360.8 17.7
            316.6C7.1 311.3 .3 300.7 0 288.9s5.9-22.8
            16.1-28.7l448-256c10.7-6.1 23.9-5.5 34 1.4z"/>
        </svg>`,
    }
];

return msg;

if (!msg.provideUi) {
    msg.provideUi = {};
}

msg.provideUi.chatElements = (elementId, history) => {
    // Get chat history from msg.chat.history property
    // The history is messages from an OpenAI API, which is an array of message objects
    // Each message object has 'role' and 'content' properties
    let chatHistory = [];
    if (history && Array.isArray(history.entries)) {
        chatHistory = history.entries;
    }

    // Create the chat history div
    const chatHistoryDiv = {
        'type': 'div',
        'id': `${elementId}-chat-history`,
        'attributes': {
            'class': 'chat-history'
        },
        'components': [],
    }

    // Add the chat history to the chat history div with separate spans for role and message
    chatHistory.forEach(message => {
        // ignore context
        if (message.role == "context") {
            return;
        }

        chatHistoryDiv.components.push({
            'type': 'p',
            'id': `entry-${message.id}`,
            'attributes': {
                'class': 'entry'
            },
            'components': [
                {
                    'type': 'span',
                    'slot': message.role,
                    'attributes': {
                        'class': 'role'
                    }
                },
                {
                    'type': 'span',
                    'id': `message-${message.id}`,
                    'slot': msg.parseMarkdown(message.content),
                    'attributes': {
                        'class': 'message'
                    }
                }
            ],
        })
    });

    return [chatHistoryDiv];
}

return msg;

// we need both a prompt and a chat
if (!msg.systemPrompt || typeof msg.systemPrompt != "string") {
    return {
        type: "model-error",
        error: "OpenAIChatCompletionsStreaming - Prompt is required to run this model."
    };
}

if (!msg.history || !Array.isArray(msg.history.entries)) {
    return {
        type: "model-error",
        error: "OpenAIChatCompletionsStreaming - History is required to run this model."
    };
}

// if we have context, we need a context prompt
if (
    msg.history.entries.find(it => it.role == "context") &&
    (!msg.contextPrompt || typeof msg.contextPrompt != "function")
) {
    return {
        type: "model-error",
        error: "OpenAIChatCompletionsStreaming - Context prompt is required for histories containing context."
    };
}

// we have both a prompt and history, build the request payload
const id = env.get("Id");

msg[id] = {
    payload: {
        "messages": [
            {
                "role": "system",
                "content": msg.systemPrompt
            },
            ...msg.history.entries.map(
                it => it.role == "context" ? ({
                    role: "system",
                    content: msg.contextPrompt(it.content)
                }) : ({
                    role: it.role,
                    content: it.content
                })
            )
        ],
        "temperature": env.get("Temperature"),
        "stop": env.get("Stop") || null,
        "stream": true
    }
}

// if we were given a model
const model = env.get("Model") || msg.model;
if (model && typeof model == "string") {
    msg[id].payload.model = model;
}

return msg;

return [[
    {
        action: "model-streaming-status",
        state: "CONNECTING"
    },
    {
        action: "model-connecting",
        type: "chat-completions-streaming",
        id: env.get("Id"),
        historyId: msg.msgIn.historyId
    }
]];

// parse our message
let content = ''
try {
    const response = JSON.parse(msg.payload);
    content = response.choices[0].delta.content;
}
catch (e) {
    // note this, but do nothing
    node.log(`Model - Error parsing JSON: ${e}`);
    return;
}

// if we have no content
if (!content) {
    // do nothing
    return;
}

return [[
    {
        action: "model-streaming-content",
        content
    },
    {
        action: "model-content",
        type: "chat-completions-streaming",
        id: env.get("Id"),
        historyId: msg.msgIn.historyId,
        content
    }
]];

return [[
    {
        action: "model-streaming-status",
        state: "DONE"
    },
    {
        action: "model-done",
        type: "chat-completions-streaming",
        id: env.get("Id"),
        historyId: msg.msgIn.historyId
    }
]];

return {
    action: "model-error",
    type: "chat-completions-streaming",
    id: env.get("Id"),
    historyId: msg.msgIn.historyId,
    error: msg.error
};

const batchSize = env.get("Batch");

// ignore legacy actions
if (["model-streaming-content", "model-streaming-status"].includes(msg.action)) {
    // we'll resend the legeacy actions
    return;
}

let messageBuffer = context.get("messageBuffer");
if (!messageBuffer) {
    messageBuffer = []
}

const makeBatch = () => {
    const batchedContent = messageBuffer.reduce((content, message) => content + message.content, "");
    const batchedMessages = [
        {
            action: "model-content",
            type: messageBuffer[0].type,
            id: messageBuffer[0].id,
            historyId: messageBuffer[0].historyId,
            content: batchedContent
        },
        {
            action: "model-streaming-content",
            content: batchedContent
        }
    ];
    context.set("messageBuffer", []);
    return batchedMessages;
}

// if we are stopping
if (msg.action == "model-done") {
    // send the rest of our current batch before sending our status
    const messages = [msg, {
        action: "model-streaming-status",
        state: "DONE"
    }];
    if (messageBuffer.length > 0) {
        messages.unshift(...makeBatch());
    }
    return [messages];
}

messageBuffer.push(msg);
context.set("messageBuffer", messageBuffer);
if (messageBuffer.length === batchSize) {
    return [makeBatch()];
}

return {
    type: "set-streaming-status",
    history: msg.history
};

