const chatId = "chat-01"

const nextMessage = msg.payload[`${chatId}-next-message`]

if (!nextMessage) {
    return;
}

msg.entry = {
    role: "user",
    content: nextMessage
};

msg.action = "history-add-entry";

return msg;

if (msg._ui?.type === "eventSend") {
    msg.type = "rerender-form"
    return msg;
}

const promptId = "prompt-01"

const newPrompt = msg.payload[`${promptId}-prompt`]

if (!newPrompt) {
    return;
}

msg.prompt = newPrompt;
msg.action = "prompt-update";

return msg;

try {
    msg.payload = JSON.parse(msg.payload);
}
catch (e) {
    throw new Error(`Error parsing history file (${msg.historyDbFilename}): ${e} - content:

    \`${msg.payload}\`
    `);
}

return msg;

const { readFileSync } = fs;

msg.historyId = env.get("Id") || msg.id;
const filename = msg.historyDbFilenames[msg.historyId]

try {
    msg.payload = readFileSync(filename).toString().trim();
    if (!msg.payload) {
        throw new Error("HistoryDb file empty.");
    }
    node.log(`Parsed history file content: \`${msg.payload}\``);
}
catch (e) {
    throw new Error(`Error reading historyDb (id: ${msg.historyId}) file (${filename}): ${e}`);
}

return msg;

const { writeFileSync } = fs;

const id = env.get("Id") || msg.id;
const filename = msg.historyDbFilenames[id]

try {
    writeFileSync(filename, msg.payload);
}
catch (e) {
    throw new Error(`Error writing historyDb file (${filename}): ${e}`);
}

return msg;

try {
    msg.payload = JSON.stringify(msg.payload);
}
catch (e) {
    throw new Error(`Error encoding history file (${msg.historyDbFilename}): ${e} - content:

    \`${msg.payload}\`
    `);
}

return msg;

const escapeHTML = str => {
    let inCodeBlock = false;
    let escapedStr = '';

    for (let i = 0; i < str.length; i++) {
        if (str[i] === '`' && str[i - 1] !== '`') {
            inCodeBlock = !inCodeBlock;
        }

        if (!inCodeBlock) {
            switch (str[i]) {
                case '&':
                    escapedStr += '&amp;';
                    break;
                case '<':
                    escapedStr += '&lt;';
                    break;
                case '>':
                    escapedStr += '&gt;';
                    break;
                case "'":
                    escapedStr += '&#39;';
                    break;
                case '"':
                    escapedStr += '&quot;';
                    break;
                default:
                    escapedStr += str[i];
            }
        } else {
            escapedStr += str[i];
        }
    }

    return escapedStr;
};

const DOMPurify = createDOMPurify(new jsdom.JSDOM('').window);
msg.parseMarkdown = (md) =>
    DOMPurify.sanitize(marked.parse(escapeHTML(md)));

return msg;

// // check for any pending content
// const pendingContent = context.get("pendingContent") ?? '';
// context.set("pendingContent", '');

const newEntry = msg.historyOps(msg.history).insert({
    role: "assistant",
    content: ''
});

msg.history.streaming = newEntry.id;
msg.action = "history-update-streaming";
msg.historyStreaming = msg.history.streaming;

return [[
  msg,
  {
    ...msg,
    action: "history-add-entry",
    newEntry
