let endpoint = flow.get('portainer_endpoint');
let container = flow.get('portainer_container');
if (!!endpoint && !!container){
    msg.url = flow.get('server_url') +'/api/endpoints/' + endpoint + '/docker/containers/' + container +'/stats?stream=false&one-shot=false';
    msg.token = flow.get('portainer_jwt');
    return msg
}
else
    return null;

var stats = msg.payload;
if(!!stats){
    var cpu_delta = stats.cpu_stats.cpu_usage.total_usage - stats.precpu_stats.cpu_usage.total_usage;
    var system_cpu_delta = stats.cpu_stats.system_cpu_usage - stats.precpu_stats.system_cpu_usage;
    var number_cpus = stats.cpu_stats.online_cpus;
   // msg.payload = { data: [stats, cpu_delta, system_cpu_delta, number_cpus]}
    msg.payload = Number((cpu_delta === 0? 0 : (cpu_delta / system_cpu_delta) * number_cpus * 100.0).toFixed(2));
    return msg;
}
else
    return null

var stats = msg.payload;
if(!!stats){
    if (!!stats.memory_stats.stats.cache)
        var used_memory = stats.memory_stats.usage - stats.memory_stats.stats.cache;
    else
        var used_memory = stats.memory_stats.usage - stats.memory_stats.stats.inactive_file
    var available_memory =stats.memory_stats.limit
    // msg.payload = { data: [stats, used_memory, available_memory]}
    msg.payload = Number(((used_memory / available_memory) * 100.000).toFixed(2));
    return msg;
}
else
return null

